""" basic event. """

import obj

class Event(obj.command.Command):

    def ok(self, txt):
        self.reply("ok %s" % txt)

    def prompt(self):
        pass

    def reply(self, txt):
        self._result.append(txt)

    def say(self, orig, channel, txt, type=None):
        if self.verbose:
            print(txt)

    def show(self):
        for txt in self._result:
            self.say(self.orig, self.origin, txt)